mapper.select:
select ut.user_id, ut.email, ut.password, ut.username, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id AS urt_user_id, urt.role_id AS urt_role_id,
rt.role_id, rt.role_name, rt.role_name_kor from user_tb as ut join user_role_tb AS urt on (urt.user_id = ut.user_id) join role_tb AS rt on (rt.role_id = urt.role_id) where ut.user_id = #{userId};

select ut.user_id, ut.email, ut.password, ut.username, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name, rt.role_name_kor from user_tb as ut join user_role_tb as urt on (urt.user_id = ut.user_id) join role_tb as rt on (rt.role_id = urt.role_id) where ut.email = #{email};

select ut.user_id, ut.email, ut.password, ut.username, ut.create_dt, ut.update_dt,
urt.user_role_id, urt.user_id as urt_user_id, urt.role_id as urt_role_id,
rt.role_id, rt.role_name, rt.role_name_kor from user_tb as ut join user_role_tb as urt on (urt.user_id = ut.user_id) join role_tb as rt on (rt.role_id = urt.role_id) where ut.username = #{username};
